const initialFormValue = {done : false ,text : ""};  

function Form({todos, setTodos}) {

  const [form , setForm] = useState(initialFormValue);   // defaul olarak tanımladığımız objeti forma tanımlandık


  console.log(todos);

  return (
    <div>
        <input type="text" placeholder ="input olacak" value ={form.text} onKeyPress={(e) => {
                                                                            if (e.key === "Enter") {
                                                                                this.setState({ message: e.target.value },
                                                                                () => {
                                                                                    alert(this.state.message);
                                                                                });
                                                                            }}}
        />        
    </div>
  )
}

export default Form
