import {useState} from 'react'

const initialFormValue = {done : false ,text : ""};  

function Form({todos, setTodos}) {

  const [form , setForm] = useState(initialFormValue);   // defaul olarak tanımladığımız objeti forma tanımlandık

  const keyHandle =  (e) => {
    if (e.key === "Enter"){
        setForm({...form, [e.target.name] : e.target.value})
    }
  }

  console.log(todos);

  return (
    <div>
        <input type="text" placeholder ="input olacak" value ={form.text} onKeyPress={keyHandle} />        
    </div>
  )
}

export default Form
